require "colorize"; stations = { "N" => ["Times Square", "34th", "28th", "23rd", "Union Square", "8th"],"L" => ["8th", "6th", "Union Square", "3rd", "1st"],"6" => ["Grand Central", "33rd", "28th", "23rd", "Union Square", "Astor Place"],:stops => 0}; loop { puts "Welcome to MTA!".light_green; puts "Which line are you starting at?".light_blue; start_line = gets.chomp.upcase; puts "Which station are you starting at?".light_blue; start_station = gets.chomp; puts "Which line are you departing at?".light_blue; exit_line = gets.chomp.upcase; puts "Which station are you departing at?".light_blue; exit_station = gets.chomp; puts "Travel on the #{start_line} line for the following stops: ".light_magenta; if start_line == exit_line; stops = stations[start_line].index(start_station) < stations[start_line].index(exit_station) ? stations[start_line][((stations[start_line].index start_station) + 1)..(stations[start_line].index exit_station)] : stations[start_line][(stations[start_line].index exit_station)..((stations[start_line].index start_station) - 1)].reverse; puts stops; stations[:stops] += stops.size; puts "Arrive at #{exit_station} on the #{exit_line} line".green; else; stops = stations[start_line].index(start_station) < stations[start_line].index("Union Square") ? stations[start_line][((stations[start_line].index start_station) + 1)..(stations[start_line].index "Union Square")] : stations[start_line][(stations[start_line].index "Union Square")..((stations[start_line].index start_station) - 1)].reverse; puts stops; stations[:stops] += stops.size; (if exit_station == "Union Square"; output = ("Arrive at #{exit_station} on the #{exit_line} line").green; puts output; next; else; puts ("Change at Union Square").light_magenta end); puts "Keep travelling on the #{exit_line} line for the following stops:".light_magenta; stops = stations[exit_line].index("Union Square") < stations[exit_line].index(exit_station) ? stations[exit_line][((stations[exit_line].index "Union Square") + 1)..(stations[exit_line].index exit_station)] : stations[exit_line][(stations[exit_line].index exit_station)..((stations[exit_line].index "Union Square") - 1)].reverse; puts stops; stations[:stops] += stops.size; puts "Arrive at #{exit_station} on the #{exit_line} line".green end; puts "Total stops: #{stations[:stops]}".green; puts "Thank you for using MTA!".blue; puts ("-=" * 40).blue }
